import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Api_products_by_category = ({ matheloai }) => {
    const [truyens, setTruyens] = useState([]); // Lưu danh sách truyện
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (!matheloai) {
            setError('Mã thể loại không hợp lệ');
            setLoading(false);
            return;
        }

        // Gọi API để lấy danh sách truyện theo mã thể loại
        axios.get(`http://localhost:3001/truyens`)
            .then(response => {
                setTruyens(response.data); // Lưu dữ liệu truyện vào state
                setLoading(false); // Đặt trạng thái loading là false
            })
            .catch(() => {
                setError('Có lỗi xảy ra khi lấy dữ liệu truyện'); // Xử lý lỗi khi gọi API
                setLoading(false); // Đặt trạng thái loading là false
            });
    }, [matheloai]); // Gọi lại khi matheloai thay đổi

    // Kiểm tra các trạng thái và trả về giao diện tương ứng
    if (loading) return <div>Đang tải...</div>;
    if (error) return <div>{error}</div>;
    if (truyens.length === 0) return <div>Không có truyện nào cho thể loại này</div>;

    return (
        <div>
            <h2>Danh sách truyện theo thể loại</h2>
            <ul>
                {truyens.map(truyen => (
                    <li key={truyen.id}>
                        <h3>{truyen.tentruyen}</h3>
                        <p><strong>Tác giả:</strong> {truyen.tacgia}</p>
                        <p><strong>Giá bán:</strong> {truyen.giaban} VNĐ</p>
                        <p><strong>Thể loại:</strong> {truyen.matheloai}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Api_products_by_category;
