import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios'; // Import axios here

const Payment = () => {
    const [paymentMethod, setPaymentMethod] = useState('Thanh toán khi nhận hàng');
    const [cities, setCities] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [wards, setWards] = useState([]);
    const [selectedCityId, setSelectedCityId] = useState('');
    const [selectedDistrictId, setSelectedDistrictId] = useState('');
    const [selectedWardId, setSelectedWardId] = useState('');

    useEffect(() => {
        axios.get("https://raw.githubusercontent.com/kenzouno1/DiaGioiHanhChinhVN/master/data.json")
            .then(response => {
                setCities(response.data);
            })
            .catch(error => {
                console.error("Error fetching cities:", error);
            });
    }, []);

    useEffect(() => {
        if (selectedCityId) {
            const selectedCity = cities.find(city => city.Id === selectedCityId);
            if (selectedCity && selectedCity.Districts) {
                setDistricts(selectedCity.Districts);
                setWards([]);
                setSelectedDistrictId('');
                setSelectedWardId('');
            } else {
                setDistricts([]);
                setWards([]);
                setSelectedDistrictId('');
                setSelectedWardId('');
            }
        } else {
            setDistricts([]);
            setWards([]);
            setSelectedDistrictId('');
            setSelectedWardId('');
        }
    }, [selectedCityId, cities]);

    useEffect(() => {
        if (selectedCityId && selectedDistrictId) {
            const selectedCity = cities.find(city => city.Id === selectedCityId);
            if (selectedCity && selectedCity.Districts) {
                const selectedDistrict = selectedCity.Districts.find(
                    district => district.Id === selectedDistrictId
                );
                if (selectedDistrict && selectedDistrict.Wards) {
                    setWards(selectedDistrict.Wards);
                    setSelectedWardId('');
                } else {
                    setWards([]);
                    setSelectedWardId('');
                }
            } else {
                setWards([]);
                setSelectedWardId('');
            }
        } else {
            setWards([]);
            setSelectedWardId('');
        }
    }, [selectedCityId, selectedDistrictId, cities]);

    const handlePaymentMethodChange = (event) => {
        setPaymentMethod(event.target.value);
    };

    const handleCityChange = (event) => {
        setSelectedCityId(event.target.value);
    };

    const handleDistrictChange = (event) => {
        setSelectedDistrictId(event.target.value);
    };

    const handleWardChange = (event) => {
        setSelectedWardId(event.target.value);
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    {/* ... Your product list rendering ... */}
                </div>
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        value={selectedCityId}
                        onChange={handleCityChange}
                    >
                        <option value="" disabled>
                            Chọn tỉnh thành
                        </option>
                        {cities.map(city => (
                            <option key={city.Id} value={city.Id}>
                                {city.Name}
                            </option>
                        ))}
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        value={selectedDistrictId}
                        onChange={handleDistrictChange}
                        disabled={!selectedCityId}
                    >
                        <option value="" disabled>
                            Chọn quận huyện
                        </option>
                        {districts.map(district => (
                            <option key={district.Id} value={district.Id}>
                                {district.Name}
                            </option>
                        ))}
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        value={selectedWardId}
                        onChange={handleWardChange}
                        disabled={!selectedDistrictId}
                    >
                        <option value="" disabled>
                            Chọn phường xã
                        </option>
                        {wards.map(ward => (
                            <option key={ward.Id} value={ward.Id}>
                                {ward.Name}
                            </option>
                        ))}
                    </select>
                    {/* ... rest of your form ... */}
                </form>
            </div>
        </div>
    );
};

export default Payment;
