import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../../component/grid.css';
import '../product/product.css';
import '../../component/style.css';
import '../product/responsive.css';
import thangsachkimdong1 from '../img/thangsachkimdong1.jpg';
import updateCartCount from '../updateCartCount';

function Product() {
    const { id_truyen } = useParams(); // Lấy id_truyen từ URL
    const [truyen, setTruyen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [theloaiName, setTheloaiName] = useState(''); // Lưu tên thể loại

    // Hàm để lấy tên thể loại từ mã thể loại
    const fetchTheloaiName = (matheloai) => {
        axios.get(`http://localhost:3001/theloais/${matheloai}`)
            .then(response => {
                setTheloaiName(response.data[0].tentheloai); // Lưu tên thể loại vào state

            })
            .catch(() => {
                setTheloaiName('Chưa có tên thể loại'); // Xử lý khi có lỗi
            });
    };

    useEffect(() => {
        updateCartCount();
        EventInputTypeNumber();

        // Gọi API để lấy dữ liệu truyện
        axios.get(`http://localhost:3001/truyens/${id_truyen}`)
            .then(response => {
                console.log('Dữ liệu API:', response.data); // Kiểm tra dữ liệu
                setTruyen(response.data); // Lưu dữ liệu truyện vào state
                setLoading(false); // Đặt trạng thái loading là false

                const matheloai = response.data[0].matheloai;
                fetchTheloaiName(matheloai); // Gọi hàm fetchTheloaiName
            })
            .catch(() => {
                setError('Không tồn tại truyện này'); // Xử lý lỗi khi gọi API
                setLoading(false); // Đặt trạng thái loading là false
            });
    }, [id_truyen]);

    function EventInputTypeNumber() {
        const inputSoLuong = document.querySelectorAll('.input-soluong');
        const buttonUp = document.querySelectorAll('.button-up');
        const buttonDown = document.querySelectorAll('.button-down');

        if (inputSoLuong.length > 0) {
            inputSoLuong.forEach(input => {
                input.addEventListener('input', function () {
                    input.value = input.value.replace(/[^0-9]/g, '');
                });

                input.addEventListener('keydown', function (event) {
                    if (event.key === 'Enter') {
                        input.blur();
                    }
                });

                input.addEventListener('blur', function () {
                    if (input.value === '') {
                        input.value = 1;
                    }
                });
            });
        }

        if (buttonUp.length > 0) {
            buttonUp.forEach((button, index) => {
                button.addEventListener('click', function () {
                    const input = inputSoLuong[index];
                    if (input) {
                        input.value = parseInt(input.value) + 1;
                    }
                });
            });
        }

        if (buttonDown.length > 0) {
            buttonDown.forEach((button, index) => {
                button.addEventListener('click', function () {
                    const input = inputSoLuong[index];
                    if (input && parseInt(input.value) > 1) {
                        input.value = parseInt(input.value) - 1;
                    }
                });
            });
        }
    }

    function handleThemVaoGio() {
        let GioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        const anhTruyen = document.querySelector('.main-product-img').src;
        const tenTruyen = document.querySelector('.main-product-name').innerText.trim();
        const giaBanTruyen = parseFloat(document.querySelector('.main-product-giaban').textContent.replace(/[^0-9]+/g, ""));
        const soLuongTruyen = parseInt(document.querySelector('.input-soluong').value);
        const tongTien = giaBanTruyen * soLuongTruyen;

        if (GioHang[tenTruyen]) {
            GioHang[tenTruyen].SoLuongTruyen += soLuongTruyen;
            GioHang[tenTruyen].TongTien += tongTien;
        } else {
            GioHang[tenTruyen] = {
                'AnhTruyen': anhTruyen,
                'TenTruyen': tenTruyen,
                'GiaBanTruyen': giaBanTruyen,
                'SoLuongTruyen': soLuongTruyen,
                'TongTien': tongTien
            };
        }

        localStorage.setItem("GioHang", JSON.stringify(GioHang));

        updateCartCount();
    }

    // Kiểm tra các trạng thái và trả về giao diện tương ứng
    if (loading) return <div>Đang tải...</div>;
    if (error) return <h1 className="flex-center" style={{ margin: '50px' }}>{error}</h1>;
    if (!truyen) return <div>Không tìm thấy truyện</div>;

    return (
        <>
            <div className="grid wide">
                <div className="row">
                    <div className="col c-12 m-12 l-12 duongdan">
                        <a className="a-black hover-red" href="/">Trang chủ</a>

                        <span>/</span>
                        <a className="a-black hover-red" href="">{theloaiName}</a>

                        <span>/</span>
                        <span>{truyen[0].tentruyen}</span>
                    </div>
                </div>
            </div>

            <div className="grid wide">
                <div className="row main-product">
                    <div className="col l-4 m-4 c-12">
                        <img
                            className="main-product-img"
                            src={thangsachkimdong1}
                        />
                    </div>
                    <div className="col l-8 m-8 c-12 main-thongtin">
                        <h1 className="main-product-name">
                            {truyen[0].tentruyen}
                        </h1>
                        <div className="danhgia displayflex">
                            <i
                                className="fas fa-star"
                                style={{ color: "rgb(239,237,106)", marginRight: 5 }}
                            />
                            <i
                                className="fas fa-star"
                                style={{ color: "rgb(239,237,106)", marginRight: 5 }}
                            />
                            <i
                                className="fas fa-star"
                                style={{ color: "rgb(239,237,106)", marginRight: 5 }}
                            />
                            <i
                                className="fas fa-star"
                                style={{ color: "rgb(239,237,106)", marginRight: 5 }}
                            />
                            <i
                                className="far fa-star"
                                style={{ color: "rgb(239,237,106)", marginRight: 5 }}
                            />
                            <span>98 đánh giá | Đã bán: 116</span>
                        </div>
                        <div className="giatien">
                            <span className="main-product-giaban">
                                <b>{truyen[0].giaban.toLocaleString('vi-VN')}đ</b>
                            </span>
                            <span className="main-product-giagoc">
                                <s>
                                    <b>{truyen[0].giagoc.toLocaleString('vi-VN')}đ</b>
                                </s>
                            </span>
                            <span>(Bạn đã tiết kiệm được {(truyen[0].giagoc - truyen[0].giaban).toLocaleString('vi-VN')}đ)</span>
                        </div>
                        <div className="displayflex-nomobile">
                            <ul className="chitietsanpham">
                                <li>
                                    <span className="chimuc">∙</span>Mã Kim Đồng:&nbsp;
                                    <a className="a-red" href="">
                                        <b>{truyen[0].id}</b>
                                    </a>
                                </li>
                                <li>
                                    <span className="chimuc">∙</span>ISBN: <b>{truyen[0].isbn}</b>
                                </li>
                                <li>
                                    <span className="chimuc">∙</span>Tác giả:
                                    <a className="a-red" href="">
                                        <b> {truyen[0].tacgia}</b>
                                    </a>
                                </li>
                                <li>
                                    <span className="chimuc">∙</span>Đối tượng:&nbsp;
                                    <a className="a-red" href="">
                                        <b>{truyen[0].doituong}</b>
                                    </a>
                                </li>
                                <li>
                                    <span className="chimuc">∙</span>Số trang: {truyen[0].sotrang}
                                </li>
                                <li>
                                    <span className="chimuc">∙</span>Định dạng: {truyen[0].dinhdang}
                                </li>
                                <li>
                                    <span className="chimuc">∙</span>Trọng lượng: {truyen[0].trongluong}
                                </li>
                                <li>
                                    <span className="chimuc">∙</span>Thể loại:&nbsp;
                                    <a className="a-red" href="">
                                        <b>
                                            {theloaiName}
                                        </b>
                                    </a>
                                </li>
                            </ul>
                            <div className="slmua">
                                <span style={{ lineHeight: 2 }}>
                                    <b>Số lượng</b>
                                </span>{" "}
                                <br />
                                <div className="ArrowInputTypeNumber">
                                    <button className="button-down flex-center">
                                        <i className="fas fa-chevron-down" />
                                    </button>
                                    <input type="text" className="input-soluong" defaultValue={1} />
                                    <button className="button-up flex-center">
                                        <i className="fas fa-chevron-up" />
                                    </button>
                                </div>
                                <button type="submit" className="btnThem" onClick={handleThemVaoGio}>
                                    <b>THÊM VÀO GIỎ HÀNG</b>
                                </button>
                                <button type="submit" className="btnMua">
                                    <b>MUA NGAY</b>
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div >
        </>
    );
}
export default Product;
