import React, { useEffect, useState } from "react";
import axios from "axios";

const TruyenTheoTheLoai = ({ matheloai, soluong = 5, on_xemthem = true }) => {
    const [truyens, setTruyens] = useState([]);
    const [tentheloai, setTenTheLoai] = useState("");
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [truyensRes, theloaiRes] = await Promise.all([
                    axios.get("http://localhost:3001/truyens"),
                    axios.get("http://localhost:3001/theloais")
                ]);

                const filtered = truyensRes.data.filter(item => item.matheloai === matheloai);
                setTruyens(filtered);

                const theloai = theloaiRes.data.find(tl => tl.matheloai === matheloai);
                console.log(theloai);

                if (theloai) setTenTheLoai(theloai.tentheloai);
            } catch (error) {
                console.error("Lỗi khi tải dữ liệu:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [matheloai]);

    if (loading) return <p>Đang tải...</p>;
    if (truyens.length === 0) return null;

    return (
        <div className="grid wide list-product">
            <h2 style={{ margin: "10px auto" }}>{tentheloai}</h2>
            <div className="row list-item">
                {truyens.slice(0, soluong).map(truyen => (
                    <div key={truyen.id} className="col l-2-4 m-3 c-4 checkSizeMobile">
                        <div className="product">
                            <a href={`/truyen/${truyen.id}`} className="a-black">
                                <div className="product-img">
                                    <img src={truyen.hinhanh} alt={truyen.tentruyen} className="product-img" />
                                </div>
                                <h4 className="product-name">{truyen.tentruyen}</h4>
                                <div className="product-price">
                                    <span className="current-price"><b>{Number(truyen.giaban).toLocaleString()}₫</b></span>
                                    {truyen.giagoc &&
                                        <span className="original-price">
                                            <s><b>{Number(truyen.giagoc).toLocaleString()}₫</b></s>
                                        </span>
                                    }
                                </div>
                            </a>
                        </div>
                    </div>
                ))}
            </div>
            {on_xemthem && (
                <div className="row">
                    <a className="col l-12 m-12 c-12 a-red xemthem" href={`/danhmuc/${matheloai}`}>
                        <span>Xem thêm &gt;&gt;</span>
                    </a>
                </div>
            )}
        </div>
    );
};

export default TruyenTheoTheLoai;
