import React, { useEffect, useRef, useState } from "react";
import '../../component/grid.css';
import styles from './banner.module.css';
import responsiveStyles from './responsive.module.css'; // Import responsive module
import '../../component/style.css';
import banner1 from '../img/banner_img1.jpg';
import banner2 from '../img/banner_img2.jpg';
import banner3 from '../img/banner_img3.jpg';
import banner4 from '../img/banner_img4.jpg';
import banner5 from '../img/banner_img5.jpg';

const Banner = () => {
    const listImgRef = useRef(null);
    const imgsRef = useRef([]);
    const btnLeftRef = useRef(null);
    const btnRightRef = useRef(null);
    const dotsRef = useRef([]);
    const [current, setCurrent] = useState(0);
    const [autoChangeInterval, setAutoChangeInterval] = useState(null);

    const updateSlider = () => {
        const listImg = listImgRef.current;
        const imgs = imgsRef.current;
        if (listImg && imgs.length > 0) {
            const width = imgs[0]?.offsetWidth || 0;
            listImg.style.transform = `translateX(${-width * current}px)`;
        }
    };

    const nextSlide = () => {
        setCurrent((prev) => (prev === imgsRef.current.length - 1 ? 0 : prev + 1));
    };

    const prevSlide = () => {
        setCurrent((prev) => (prev === 0 ? imgsRef.current.length - 1 : prev - 1));
    };

    const goToSlide = (index) => {
        setCurrent(index);
    };

    useEffect(() => {
        updateSlider();
    }, [current]);

    useEffect(() => {
        const autoChange = setInterval(nextSlide, 4000);
        setAutoChangeInterval(autoChange);

        const btnRight = btnRightRef.current;
        const btnLeft = btnLeftRef.current;

        const handleRightClick = () => {
            clearInterval(autoChangeInterval);
            nextSlide();
            setAutoChangeInterval(setInterval(nextSlide, 4000));
        };

        const handleLeftClick = () => {
            clearInterval(autoChangeInterval);
            prevSlide();
            setAutoChangeInterval(setInterval(nextSlide, 4000));
        };

        if (btnRight) {
            btnRight.addEventListener('click', handleRightClick);
        }
        if (btnLeft) {
            btnLeft.addEventListener('click', handleLeftClick);
        }

        return () => {
            clearInterval(autoChangeInterval);
            if (btnRight) {
                btnRight.removeEventListener('click', handleRightClick);
            }
            if (btnLeft) {
                btnLeft.removeEventListener('click', handleLeftClick);
            }
        };
    }, []);

    return (
        <div className={`${styles.banner} ${responsiveStyles.banner}`}>
            <div className={styles['slide-show']}>
                <div id="img-banners" className="displayflex" ref={listImgRef}>
                    {[banner1, banner2, banner3, banner4, banner5].map((banner, index) => (
                        <img
                            key={index}
                            className={styles['img-banner']}
                            src={banner}
                            ref={(el) => (imgsRef.current[index] = el)}
                        />
                    ))}
                </div>
            </div>
            <div className={`${styles['btns-banner']} ${responsiveStyles['btns-banner']}`}>
                <div className={`${styles['btn-banner']} icon-white ${styles['btn-left']} ${responsiveStyles['btn-banner']} ${responsiveStyles['btn-left']}`} ref={btnLeftRef}>
                    <i className="fas fa-chevron-left" />
                </div>
                <div className={`${styles['btn-banner']} icon-white ${styles['btn-right']} ${responsiveStyles['btn-banner']} ${responsiveStyles['btn-right']}`} ref={btnRightRef}>
                    <i className="fas fa-chevron-right" />
                </div>
            </div>
            <div className={`${styles.dots} ${responsiveStyles.dots}`}>
                {[...Array(5)].map((_, index) => (
                    <div
                        key={index}
                        className={`${styles.dot} ${responsiveStyles.dot} ${index === current ? `${styles.active} ${responsiveStyles.active}` : ''}`}
                        ref={(el) => (dotsRef.current[index] = el)}
                        onClick={() => goToSlide(index)}
                    />
                ))}
            </div>
        </div>
    );
};

export default Banner;
