import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import styles from './payment.module.css'; // Giả sử bạn có file CSS

const Payment = () => {
    const [tinhTPData, setTinhTPData] = useState([]);
    const [quanHuyenData, setQuanHuyenData] = useState([]);
    const [xaPhuongData, setXaPhuongData] = useState([]);

    const [selectedTinhTP, setSelectedTinhTP] = useState('');
    const [selectedQuanHuyen, setSelectedQuanHuyen] = useState('');
    const [selectedXaPhuong, setSelectedXaPhuong] = useState('');

    const tinhTPRef = useRef(null);
    const quanHuyenRef = useRef(null);
    const xaPhuongRef = useRef(null);

    useEffect(() => {
        // Hàm để tải dữ liệu Tỉnh/TP (thay thế đường dẫn JSON của bạn)
        const loadTinhTP = async () => {
            try {
                const response = await axios.get('/data/tinh_tp.json');
                setTinhTPData(response.data);
            } catch (error) {
                console.error("Lỗi khi tải dữ liệu Tỉnh/TP:", error);
            }
        };

        loadTinhTP();
    }, []);

    useEffect(() => {
        // Hàm để tải dữ liệu Quận/Huyện dựa trên Tỉnh/TP đã chọn (thay thế đường dẫn và logic lọc của bạn)
        const loadQuanHuyen = async (maTinhTP) => {
            if (maTinhTP) {
                try {
                    const response = await axios.get(`/data/quan_huyen_${maTinhTP}.json`);
                    setQuanHuyenData(response.data);
                    setSelectedQuanHuyen(''); // Reset Quận/Huyện khi Tỉnh/TP thay đổi
                    setXaPhuongData([]);     // Reset Xã/Phường
                    setSelectedXaPhuong('');
                } catch (error) {
                    console.error("Lỗi khi tải dữ liệu Quận/Huyện:", error);
                }
            } else {
                setQuanHuyenData([]);
                setXaPhuongData([]);
                setSelectedQuanHuyen('');
                setSelectedXaPhuong('');
            }
        };

        loadQuanHuyen(selectedTinhTP);
    }, [selectedTinhTP]);

    useEffect(() => {
        // Hàm để tải dữ liệu Xã/Phường dựa trên Quận/Huyện đã chọn (thay thế đường dẫn và logic lọc của bạn)
        const loadXaPhuong = async (maQuanHuyen) => {
            if (maQuanHuyen) {
                try {
                    const response = await axios.get(`/data/xa_phuong_${maQuanHuyen}.json`);
                    setXaPhuongData(response.data);
                    setSelectedXaPhuong(''); // Reset Xã/Phường khi Quận/Huyện thay đổi
                } catch (error) {
                    console.error("Lỗi khi tải dữ liệu Xã/Phường:", error);
                }
            } else {
                setXaPhuongData([]);
                setSelectedXaPhuong('');
            }
        };

        loadXaPhuong(selectedQuanHuyen);
    }, [selectedQuanHuyen]);

    const handleTinhTPChange = (event) => {
        setSelectedTinhTP(event.target.value);
    };

    const handleQuanHuyenChange = (event) => {
        setSelectedQuanHuyen(event.target.value);
    };

    const handleXaPhuongChange = (event) => {
        setSelectedXaPhuong(event.target.value);
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                {/* ... (phần sản phẩm và giảm giá) ... */}
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        ref={tinhTPRef}
                        value={selectedTinhTP}
                        onChange={handleTinhTPChange}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {tinhTPData.map(item => (
                            <option key={item.id} value={item.id}>{item.name}</option>
                        ))}
                    </select>

                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        ref={quanHuyenRef}
                        value={selectedQuanHuyen}
                        onChange={handleQuanHuyenChange}
                        disabled={!selectedTinhTP} // Vô hiệu hóa nếu chưa chọn Tỉnh/TP
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {quanHuyenData.map(item => (
                            <option key={item.id} value={item.id}>{item.name}</option>
                        ))}
                    </select>

                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        ref={xaPhuongRef}
                        value={selectedXaPhuong}
                        onChange={handleXaPhuongChange}
                        disabled={!selectedQuanHuyen} // Vô hiệu hóa nếu chưa chọn Quận/Huyện
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {xaPhuongData.map(item => (
                            <option key={item.id} value={item.id}>{item.name}</option>
                        ))}
                    </select>
                    {/* ... (các input và button khác) ... */}
                </form>
            </div>
        </div>
    );
};

export default Payment;
