import React, { useState } from 'react';
import './ArrowInputTypeNumber.css';

const ArrowInputTypeNumber = ({ defaultValue = 1, onChange }) => {
    const [value, setValue] = useState(defaultValue);

    const handleInputChange = (e) => {
        const newValue = e.target.value.replace(/[^0-9]/g, '');
        setValue(newValue === '' ? '' : parseInt(newValue));
        if (onChange) onChange(newValue === '' ? 1 : parseInt(newValue));
    };

    const handleBlur = () => {
        if (value === '' || value <= 0) {
            setValue(1);
            if (onChange) onChange(1);
        }
    };

    const increase = () => {
        const newVal = parseInt(value || 1) + 1;
        setValue(newVal);
        if (onChange) onChange(newVal);
    };

    const decrease = () => {
        const currentVal = parseInt(value || 1);
        const newVal = currentVal > 1 ? currentVal - 1 : 1;
        setValue(newVal);
        if (onChange) onChange(newVal);
    };

    return (
        <div className="ArrowInputTypeNumber">
            <button className="button-down flex-center" onClick={decrease}>
                <i className="fas fa-chevron-down" />
            </button>
            <input
                type="text"
                className="input-soluong"
                value={value}
                onChange={handleInputChange}
                onBlur={handleBlur}
                onKeyDown={(e) => e.key === 'Enter' && e.target.blur()}
            />
            <button className="button-up flex-center" onClick={increase}>
                <i className="fas fa-chevron-up" />
            </button>
        </div>
    );
};

export default ArrowInputTypeNumber;
