import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';

const Payment = () => {
    const [paymentMethod, setPaymentMethod] = useState('Thanh toán khi nhận hàng');
    const [cities, setCities] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [wards, setWards] = useState([]);
    const [selectedCity, setSelectedCity] = useState('');
    const [selectedDistrict, setSelectedDistrict] = useState('');
    const [selectedWard, setSelectedWard] = useState('');
    const [gioHangObject, setGioHangObject] = useState({});
    const [gioHangArray, setGioHangArray] = useState([]);
    const [maGiamGiaInput, setMaGiamGiaInput] = useState('');
    const [maGiamGia, setMaGiamGia] = useState(null);
    const [phanTramGiamGia, setPhanTramGiamGia] = useState(0);
    const [thongBaoGiamGia, setThongBaoGiamGia] = useState('');

    useEffect(() => {
        const storedCart = localStorage.getItem('GioHang');
        if (storedCart) {
            try {
                const parsedCart = JSON.parse(storedCart);
                setGioHangObject(parsedCart);
                setGioHangArray(Object.values(parsedCart));
            } catch (error) {
                console.error('Lỗi khi parse dữ liệu giỏ hàng:', error);
                setGioHangObject({});
                setGioHangArray([]);
            }
        } else {
            setGioHangObject({});
            setGioHangArray([]);
        }

        fetch('https://provinces.open-api.vn/api/')
            .then(response => response.json())
            .then(data => setCities(data))
            .catch(error => console.error('Lỗi khi lấy dữ liệu tỉnh/thành phố:', error));
    }, []);

    useEffect(() => {
        if (selectedCity) {
            fetch(`https://provinces.open-api.vn/api/p/${selectedCity}?depth=2`)
                .then(response => response.json())
                .then(data => {
                    if (data?.districts) {
                        setDistricts(Object.values(data.districts));
                        setWards([]);
                        setSelectedDistrict('');
                        setSelectedWard('');
                    } else {
                        setDistricts([]);
                        setWards([]);
                        setSelectedDistrict('');
                        setSelectedWard('');
                    }
                })
                .catch(error => console.error('Lỗi khi lấy dữ liệu quận/huyện:', error));
        } else {
            setDistricts([]);
            setWards([]);
            setSelectedDistrict('');
            setSelectedWard('');
        }
    }, [selectedCity]);

    useEffect(() => {
        if (selectedDistrict && selectedCity) {
            fetch(`https://provinces.open-api.vn/api/d/${selectedDistrict}?depth=2`)
                .then(response => response.json())
                .then(data => {
                    if (data?.wards) {
                        setWards(Object.values(data.wards));
                        setSelectedWard('');
                    } else {
                        setWards([]);
                        setSelectedWard('');
                    }
                })
                .catch(error => console.error('Lỗi khi lấy dữ liệu phường/xã:', error));
        } else {
            setWards([]);
            setSelectedWard('');
        }
    }, [selectedDistrict, selectedCity]);

    const handlePaymentMethodChange = (event) => {
        setPaymentMethod(event.target.value);
    };

    const handleCityChange = (event) => {
        setSelectedCity(event.target.value);
    };

    const handleDistrictChange = (event) => {
        setSelectedDistrict(event.target.value);
    };

    const handleWardChange = (event) => {
        setSelectedWard(event.target.value);
    };

    const handleMaGiamGiaInputChange = (event) => {
        setMaGiamGiaInput(event.target.value);
        setThongBaoGiamGia('');
        setMaGiamGia(null);
        setPhanTramGiamGia(0);
    };

    const handleApDungGiamGia = () => {
        const maNhap = maGiamGiaInput.trim().toUpperCase();
        if (maNhap) {
            axios.get('http://localhost:3001/giamgias/')
                .then(response => {
                    const danhSachGiamGia = response.data;
                    const maKhuyenMaiHopLe = danhSachGiamGia.find(
                        (gg) => gg.id.toUpperCase() === maNhap &&
                            new Date(gg.ngaybatdau) <= new Date() &&
                            new Date(gg.ngayketthuc) >= new Date()
                    );

                    if (maKhuyenMaiHopLe) {
                        setMaGiamGia(maKhuyenMaiHopLe);
                        setPhanTramGiamGia(maKhuyenMaiHopLe.phantramgiamgia);
                        setThongBaoGiamGia(`Áp dụng mã giảm giá ${maNhap} thành công! Giảm ${maKhuyenMaiHopLe.phantramgiamgia}%`);
                    } else {
                        setMaGiamGia(null);
                        setPhanTramGiamGia(0);
                        setThongBaoGiamGia(`Mã giảm giá ${maNhap} không hợp lệ hoặc đã hết hạn!`);
                    }
                })
                .catch(error => {
                    console.error('Lỗi khi lấy dữ liệu giảm giá:', error);
                    setMaGiamGia(null);
                    setPhanTramGiamGia(0);
                    setThongBaoGiamGia('Có lỗi xảy ra khi kiểm tra mã giảm giá.');
                });
        } else {
            setMaGiamGia(null);
            setPhanTramGiamGia(0);
            setThongBaoGiamGia('Vui lòng nhập mã giảm giá.');
        }
    };

    const calculateTotalPrice = () => {
        const tongTienHang = Object.values(gioHangObject).reduce(
            (total, item) => total + item.GiaBanTruyen * item.SoLuongTruyen,
            0
        );
        const tienGiamGia = (tongTienHang * phanTramGiamGia) / 100;
        return tongTienHang - tienGiamGia;
    };

    const calculateTamTinh = () => {
        return Object.values(gioHangObject).reduce(
            (total, item) => total + item.GiaBanTruyen * item.SoLuongTruyen,
            0
        );
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6`}>
                    <div id="list-product" className={styles['list-product']}>
                        {gioHangArray.length > 0 ? (
                            gioHangArray.map(item => (
                                <div className={styles['product-item']} key={item.ID}>
                                    <div className={styles['product-img']}>
                                        <img src={item.AnhTruyen} alt={item.TenTruyen} />
                                        <span className={styles['soluongsp']}>{item.SoLuongTruyen}</span>
                                    </div>
                                    <div className={styles['product-info']}>
                                        <h5 className={styles['product-name']}>{item.TenTruyen}</h5>
                                        <p className={styles['product-price']}>{item.GiaBanTruyen?.toLocaleString('vi-VN')}đ</p>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>Không có sản phẩm nào trong giỏ hàng.</p>
                        )}
                    </div>
                    <div id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={maGiamGiaInput}
                            onChange={handleMaGiamGiaInputChange}
                        />
                        <button type="button" className={styles['giamgia-button']} onClick={handleApDungGiamGia}>
                            Áp dụng
                        </button>
                    </div>
                    {thongBaoGiamGia && (
                        <div className={`${maGiamGia ? styles['success-apply'] : styles['fail-apply']}`}>
                            {thongBaoGiamGia}
                        </div>
                    )}
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{calculateTamTinh().toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        {phanTramGiamGia > 0 && (
                            <p>
                                Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{((calculateTamTinh() * phanTramGiamGia) / 100).toLocaleString('vi-VN')}đ ({phanTramGiamGia}%)</span>
                            </p>
                        )}
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{calculateTotalPrice().toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleCityChange}
                        value={selectedCity}
                    >
                        <option value="" disabled>Chọn tỉnh thành</option>
                        {cities.map(city => (
                            <option key={city.code} value={city.code}>{city.name}</option>
                        ))}
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleDistrictChange}
                        value={selectedDistrict}
                        disabled={!selectedCity}
                    >
                        <option value="" disabled>Chọn quận huyện</option>
                        {districts.map(district => (
                            <option key={district.code} value={district.code}>{district.name}</option>
                        ))}
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        onChange={handleWardChange}
                        value={selectedWard}
                        disabled={!selectedDistrict}
                    >
                        <option value="" disabled>Chọn phường xã</option>
                        {wards.map(ward => (
                            <option key={ward.code} value={ward.code}>{ward.name}</option>
                        ))}
                    </select>
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                    />
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={paymentMethod === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={paymentMethod === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {paymentMethod === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
