import React, { useState, useEffect } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import { useNavigate } from 'react-router-dom';

const Payment = () => {
    // State để quản lý dữ liệu giỏ hàng (ví dụ)
    const [cartItems, setCartItems] = useState([
        { id: 1, name: 'Truyện 1', price: 20000, quantity: 2, image: '../../Asset/IMG/book_demo.jpg' },
        { id: 2, name: 'Truyện 2', price: 35000, quantity: 1, image: '../../Asset/IMG/book_demo_2.jpg' },
    ]);
    const [discountCode, setDiscountCode] = useState('');
    const [isDiscountApplied, setIsDiscountApplied] = useState(false);
    const [isDiscountInvalid, setIsDiscountInvalid] = useState(false);
    const navigate = useNavigate();
    const [city, setCity] = useState('');
    const [district, setDistrict] = useState('');
    const [ward, setWard] = useState('');
    const [specificAddress, setSpecificAddress] = useState('');
    const [paymentMethod, setPaymentMethod] = useState('Thanh toán khi nhận hàng');
    const [totalAmount, setTotalAmount] = useState(0);
    const [discountAmount, setDiscountAmount] = useState(0);
    const [shippingFee, setShippingFee] = useState(0); // Mặc định là miễn phí như trong CSS

    // Hàm tính tổng tiền tạm tính
    const calculateSubtotal = () => {
        return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    // Hàm xử lý thay đổi mã giảm giá
    const handleDiscountCodeChange = (event) => {
        setDiscountCode(event.target.value);
        setIsDiscountApplied(false);
        setIsDiscountInvalid(false);
    };

    // Hàm xử lý áp dụng mã giảm giá (ví dụ)
    const handleApplyDiscount = (event) => {
        event.preventDefault();
        // Gọi API kiểm tra mã giảm giá ở đây
        if (discountCode.toLowerCase() === 'abc') {
            setIsDiscountApplied(true);
            setIsDiscountInvalid(false);
            setDiscountAmount(calculateSubtotal() * 0.1); // Giảm 10% (ví dụ)
        } else if (discountCode) {
            setIsDiscountInvalid(true);
            setIsDiscountApplied(false);
            setDiscountAmount(0);
        } else {
            setIsDiscountInvalid(false);
            setIsDiscountApplied(false);
            setDiscountAmount(0);
        }
    };

    // Hàm tính tổng tiền cuối cùng
    useEffect(() => {
        const subtotal = calculateSubtotal();
        const finalTotal = subtotal + shippingFee - discountAmount;
        setTotalAmount(finalTotal > 0 ? finalTotal : 0);
    }, [cartItems, discountAmount, shippingFee]);

    // Hàm xử lý thay đổi phương thức thanh toán
    const handlePaymentMethodChange = (event) => {
        setPaymentMethod(event.target.value);
    };

    // Hàm xử lý đặt hàng (ví dụ)
    const handlePlaceOrder = (event) => {
        event.preventDefault();
        // Gọi API đặt hàng ở đây, gửi thông tin giỏ hàng, địa chỉ, phương thức thanh toán, tổng tiền, ...
        console.log('Thông tin đặt hàng:', {
            cartItems,
            city,
            district,
            ward,
            specificAddress,
            paymentMethod,
            totalAmount,
            discountCode: isDiscountApplied ? discountCode : null,
        });
        alert('Đặt hàng thành công!');
        // Sau khi đặt hàng thành công, có thể navigate đi trang khác
        // navigate('/order-success');
    };

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6 ${styles['sanphamdamua']}`}>
                    <div id="list-product">
                        {cartItems.map(item => (
                            <div key={item.id} className={styles['product-item']}>
                                <div className={styles['product-img']}>
                                    <img src={item.image} alt={item.name} />
                                    <span className={styles['soluongsp']}>{item.quantity}</span>
                                </div>
                                <div className={styles['product-info']}>
                                    <h5 className={styles['product-name']}>{item.name}</h5>
                                    <p className={styles['product-price']}>{item.price.toLocaleString('vi-VN')}đ</p>
                                </div>
                            </div>
                        ))}
                    </div>
                    <form id="giamgia" className={styles['giamgia']} onSubmit={handleApplyDiscount}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                            value={discountCode}
                            onChange={handleDiscountCodeChange}
                        />
                        <button type="submit" className={styles['giamgia-button']}>Áp dụng</button>
                    </form>
                    <div className={`${styles['fail-apply']} ${isDiscountInvalid ? styles['show'] : ''}`}>Mã giảm giá {discountCode} không tồn tại!</div>
                    <div className={`${styles['success-apply']} ${isDiscountApplied ? styles['show'] : ''}`}>Áp dụng mã giảm giá {discountCode} thành công!</div>
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>{calculateSubtotal().toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        <p>
                            Chiết khấu mã giảm giá <span className={styles['product-discount']}>-{discountAmount.toLocaleString('vi-VN')}đ</span>
                        </p>
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>{totalAmount.toLocaleString('vi-VN')}đ</span>
                        </p>
                    </div>
                </div>
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`} onSubmit={handlePlaceOrder}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        value={city}
                        onChange={(e) => setCity(e.target.value)}
                    >
                        <option value="" disabled>
                            Chọn tỉnh thành
                        </option>
                        {/* Dữ liệu tỉnh thành sẽ được fetch từ API hoặc có sẵn */}
                        <option value="Hanoi">Hà Nội</option>
                        <option value="Hochiminh">Hồ Chí Minh</option>
                        {/* Thêm các tỉnh thành khác */}
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        value={district}
                        onChange={(e) => setDistrict(e.target.value)}
                    >
                        <option value="" disabled>
                            Chọn quận huyện
                        </option>
                        {/* Dữ liệu quận huyện sẽ phụ thuộc vào tỉnh thành đã chọn */}
                        {city === 'Hanoi' && (
                            <>
                                <option value="BaDinh">Ba Đình</option>
                                <option value="HoanKiem">Hoàn Kiếm</option>
                                {/* Thêm các quận huyện của Hà Nội */}
                            </>
                        )}
                        {city === 'Hochiminh' && (
                            <>
                                <option value="Quan1">Quận 1</option>
                                <option value="Quan3">Quận 3</option>
                                {/* Thêm các quận huyện của Hồ Chí Minh */}
                            </>
                        )}
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        value={ward}
                        onChange={(e) => setWard(e.target.value)}
                    >
                        <option value="" disabled>
                            Chọn phường xã
                        </option>
                        {/* Dữ liệu phường xã sẽ phụ thuộc vào quận huyện đã chọn */}
                        {district === 'BaDinh' && (
                            <>
                                <option value="DienBien">Điện Biên</option>
                                <option value="QuanThanh">Quán Thánh</option>
                                {/* Thêm các phường xã của Ba Đình */}
                            </>
                        )}
                        {district === 'Quan1' && (
                            <>
                                <option value="BenNghe">Bến Nghé</option>
                                <option value="BenThanh">Bến Thành</option>
                                {/* Thêm các phường xã của Quận 1 */}
                            </>
                        )}
                    </select>
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                        value={specificAddress}
                        onChange={(e) => setSpecificAddress(e.target.value)}
                    />
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={paymentMethod === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={paymentMethod === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {paymentMethod === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center ${styles['qr-pay-image-container']}`}>
                                <img
                                    className={`img-qr-pay flex-center ${styles['qr-pay-image']}`}
                                    src="../../Asset/IMG/qr-pay.jpg"
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang-button']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
