import { useState, useEffect } from 'react';

const useCheckLogin = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [tenKhach, setTenKhach] = useState('');

    useEffect(() => {
        const storedTenKhachHang = localStorage.getItem('tenKhachHang');
        if (storedTenKhachHang) {
            setTenKhach(storedTenKhachHang);
            setIsLoggedIn(true);
        } else {
            setIsLoggedIn(false);
            setTenKhach(''); // Đảm bảo tên khách cũng được reset
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('tenKhachHang');
        setIsLoggedIn(false);
        setTenKhach('');
        // Bạn có thể thêm các hành động khác sau khi đăng xuất ở đây
        window.location.reload(); // Tải lại trang để cập nhật giao diện
    };

    // Hàm này có thể được gọi từ bên ngoài hook để cập nhật trạng thái đăng nhập
    const setAuthInfo = (tenKhachHang) => {
        setTenKhach(tenKhachHang);
        setIsLoggedIn(true);
    };

    return { isLoggedIn, tenKhach, handleLogout, setAuthInfo };
};

export default useCheckLogin;
