import React, { useState, useEffect } from "react";
import '../../component/grid.css';
import '../product/product.css';
import '../../component/style.css';
import '../product/responsive.css';
import hinhAnhSach from '../img/thangsachkimdong1.jpg';

function SanPham() {
    const [soLuongGioHang, setSoLuongGioHang] = useState(0);

    // Lấy tổng số lượng sản phẩm trong giỏ hàng từ localStorage
    function layTongSoLuongGioHang() {
        let gioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        return Object.values(gioHang).reduce((tong, sanPham) => tong + sanPham.SoLuong, 0);
    }

    // Cập nhật hiển thị số lượng trên biểu tượng giỏ hàng
    function capNhatSoLuongHienThi(soLuong) {
        let danhSachPhanTuSoLuong = document.querySelectorAll("#giohang .soluong");
        danhSachPhanTuSoLuong.forEach(phanTu => {
            phanTu.textContent = soLuong < 10 ? soLuong : "9+";
        });
    }

    // useEffect để cập nhật số lượng khi component render
    useEffect(() => {
        setSoLuongGioHang(layTongSoLuongGioHang()); // Load lại số lượng giỏ hàng
        capNhatSoLuongHienThi(layTongSoLuongGioHang());
        xuLySuKienNhapSoLuong();
    }, []);

    // Xử lý sự kiện cho input nhập số lượng sản phẩm
    function xuLySuKienNhapSoLuong() {
        const danhSachInput = document.querySelectorAll('.input-soluong');
        const danhSachNutTang = document.querySelectorAll('.button-up');
        const danhSachNutGiam = document.querySelectorAll('.button-down');

        danhSachInput.forEach(input => {
            input.addEventListener('input', function () {
                input.value = input.value.replace(/[^0-9]/g, '');
            });
            input.addEventListener('keydown', function (event) {
                if (event.key === 'Enter') input.blur();
            });
            input.addEventListener('blur', function () {
                if (input.value === '') input.value = 1;
            });
        });

        danhSachNutTang.forEach((nut, index) => {
            nut.addEventListener('click', function () {
                let input = danhSachInput[index];
                if (input) input.value = parseInt(input.value) + 1;
            });
        });

        danhSachNutGiam.forEach((nut, index) => {
            nut.addEventListener('click', function () {
                let input = danhSachInput[index];
                if (input && parseInt(input.value) > 1) {
                    input.value = parseInt(input.value) - 1;
                }
            });
        });
    }

    // Thêm sản phẩm vào giỏ hàng
    function themVaoGioHang() {
        let gioHang = JSON.parse(localStorage.getItem('GioHang')) || {};
        const anhSanPham = document.querySelector('.main-product-img').src;
        const tenSanPham = document.querySelector('.main-product-name').innerText.trim();
        const giaSanPham = parseFloat(document.querySelector('.main-product-giaban').textContent.replace(/[^0-9]+/g, ""));
        const soLuong = parseInt(document.querySelector('.input-soluong').value);
        const tongTien = giaSanPham * soLuong;

        if (gioHang[tenSanPham]) {
            gioHang[tenSanPham].SoLuong += soLuong;
            gioHang[tenSanPham].TongTien += tongTien;
        } else {
            gioHang[tenSanPham] = {
                'Anh': anhSanPham,
                'Ten': tenSanPham,
                'Gia': giaSanPham,
                'SoLuong': soLuong,
                'TongTien': tongTien
            };
        }

        localStorage.setItem("GioHang", JSON.stringify(gioHang));

        // Cập nhật tổng số lượng giỏ hàng
        const tongSoLuongMoi = layTongSoLuongGioHang();
        setSoLuongGioHang(tongSoLuongMoi);
        capNhatSoLuongHienThi(tongSoLuongMoi);
    }

    return (
        <div className="grid wide">
            <div className="row main-product">
                <div className="col l-4 m-4 c-12">
                    <img className="main-product-img" src={hinhAnhSach} alt="Sản phẩm" />
                </div>
                <div className="col l-8 m-8 c-12 main-thongtin">
                    <h1 className="main-product-name">LIỆU CÓ SAI LẦM KHI TÌM KIẾM CUỘC GẶP GỠ ĐỊNH MỆNH TRONG DUNGEON? - TẬP 13</h1>
                    <div className="giatien">
                        <span className="main-product-giaban"><b>52,500đ</b></span>
                        <span className="main-product-giagoc"><s><b>105,000đ</b></s></span>
                        <span>(Bạn đã tiết kiệm được 52,500đ)</span>
                    </div>
                    <div className="slmua">
                        <span><b>Số lượng</b></span>
                        <div className="ArrowInputTypeNumber">
                            <button className="button-down flex-center"><i className="fas fa-chevron-down" /></button>
                            <input type="text" className="input-soluong" defaultValue={1} />
                            <button className="button-up flex-center"><i className="fas fa-chevron-up" /></button>
                        </div>
                        <button className="btnThem" onClick={themVaoGioHang}><b>THÊM VÀO GIỎ HÀNG</b></button>
                        <button className="btnMua"><b>MUA NGAY</b></button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default SanPham;
