import React, { useState, useEffect, useRef } from 'react';
import styles from './payment.module.css';
import '../../component/grid.css';
import '../../component/style.css';
import qrpay from '../img/qr-pay.jpg';
import axios from 'axios';

const Payment = () => {
    const [paymentMethod, setPaymentMethod] = useState('Thanh toán khi nhận hàng');
    const cityRef = useRef(null);
    const districtRef = useRef(null);
    const wardRef = useRef(null);

    const handlePaymentMethodChange = (event) => {
        setPaymentMethod(event.target.value);
    };

    useEffect(() => {
        const citis = cityRef.current;
        const districts = districtRef.current;
        const wards = wardRef.current;

        const Parameter = {
            url: "https://raw.githubusercontent.com/kenzouno1/DiaGioiHanhChinhVN/master/data.json",
            method: "GET",
            responseType: "application/json",
        };

        axios(Parameter)
            .then(function (result) {
                renderCity(result.data);
            })
            .catch(error => {
                console.error("Lỗi khi tải dữ liệu địa chỉ:", error);
            });

        function renderCity(data) {
            for (const x of data) {
                var opt = document.createElement('option');
                opt.value = x.Name;
                opt.text = x.Name;
                opt.setAttribute('data-id', x.Id);
                citis.options.add(opt);
            }

            citis.onchange = function () {
                districts.length = 1;
                wards.length = 1;
                if (this.options[this.selectedIndex].dataset.id !== "") {
                    const result = data.filter(n => n.Id === this.options[this.selectedIndex].dataset.id);

                    for (const k of result[0].Districts) {
                        var opt = document.createElement('option');
                        opt.value = k.Name;
                        opt.text = k.Name;
                        opt.setAttribute('data-id', k.Id);
                        districts.options.add(opt);
                    }
                }
            };

            districts.onchange = function () {
                wards.length = 1;
                const dataCity = data.filter((n) => n.Id === citis.options[citis.selectedIndex].dataset.id);
                if (this.options[this.selectedIndex].dataset.id !== "") {
                    const dataWards = dataCity[0].Districts.filter(n => n.Id === this.options[this.selectedIndex].dataset.id)[0].Wards;

                    for (const w of dataWards) {
                        var opt = document.createElement('option');
                        opt.value = w.Name;
                        opt.text = w.Name;
                        opt.setAttribute('data-id', w.Id);
                        wards.options.add(opt);
                    }
                }
            };
        }

        return () => {
            if (citis) citis.onchange = null;
            if (districts) districts.onchange = null;
        };
    }, []);

    return (
        <div id="main" className="grid wide">
            <div className="row displayflex">
                <div id="sanphamdamua" className={`col c-12 m-12 l-6 ${styles['sanphamdamua']}`}>
                    <div id="list-product" className={styles['list-product']}>
                        <div className={styles['product-item']}>
                            <div className={styles['product-img']}>
                                <img src="../../Asset/IMG/book_demo.jpg" alt="Sản phẩm 1" />
                                <span className={styles['soluongsp']}>2</span>
                            </div>
                            <div className={styles['product-info']}>
                                <h5 className={styles['product-name']}>Truyện 1</h5>
                                <p className={styles['product-price']}>20.000đ</p>
                            </div>
                        </div>
                        <div className={styles['product-item']}>
                            <div className={styles['product-img']}>
                                <img src="../../Asset/IMG/book_demo_2.jpg" alt="Sản phẩm 2" />
                                <span className={styles['soluongsp']}>1</span>
                            </div>
                            <div className={styles['product-info']}>
                                <h5 className={styles['product-name']}>Truyện 2</h5>
                                <p className={styles['product-price']}>35.000đ</p>
                            </div>
                        </div>
                        {/* Thêm các sản phẩm khác vào đây */}
                    </div>
                    <form id="giamgia" className={styles['giamgia']}>
                        <input
                            type="text"
                            placeholder="Nhập mã giảm giá"
                            id="magiamgia"
                            required
                            className={styles['giamgia-input']}
                        />
                        <button type="submit" className={styles['giamgia-button']}>Áp dụng</button>
                    </form>
                    <div className={`${styles['fail-apply']}`}>Mã giảm giá abc không tồn tại!</div>
                    <div className={`${styles['success-apply']}`}>Áp dụng mã giảm giá abc thành công!</div>
                    <div className={styles['tongtien']}>
                        <p>
                            Tạm tính <span className={styles['product-tamtinh']}>75.000đ</span>
                        </p>
                        <p>
                            Phí ship <span className={styles['ship-fee']}>Miễn phí</span>
                        </p>
                        <p>
                            Chiết khấu mã giảm giá <span className={styles['product-discount']}>-0đ</span>
                        </p>
                        <p className={styles['tt']}>
                            Tổng tiền <span className={styles['product-tongtien']}>75.000đ</span>
                        </p>
                    </div>
                </div>
                <form id="main-form" className={`col c-12 m-12 l-o-1 l-5 ${styles['main-form']}`}>
                    <h1 className={`flex-center ${styles['delivery-header']}`}>ĐỊA CHỈ GIAO HÀNG</h1>
                    <select
                        id="city"
                        className={`${styles.textbox} ${styles['city-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        ref={cityRef}
                        defaultValue="" // Giá trị mặc định ban đầu
                    >
                        <option value="" disabled>
                            Chọn tỉnh thành
                        </option>
                    </select>
                    <select
                        id="district"
                        className={`${styles.textbox} ${styles['district-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        ref={districtRef}
                        defaultValue="" // Giá trị mặc định ban đầu
                    >
                        <option value="" disabled>
                            Chọn quận huyện
                        </option>
                    </select>
                    <select
                        id="ward"
                        className={`${styles.textbox} ${styles['ward-select']}`}
                        style={{ cursor: "pointer" }}
                        required
                        ref={wardRef}
                        defaultValue="" // Giá trị mặc định ban đầu
                    >
                        <option value="" disabled>
                            Chọn phường xã
                        </option>
                    </select>
                    <input
                        id="diachicuthe"
                        className={`${styles.textbox} ${styles['specific-address']}`}
                        type="text"
                        placeholder="Địa chỉ cụ thể"
                        required
                    />
                    <div id="phuongthucthanhtoan" className={styles['phuongthucthanhtoan']}>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Thanh toán khi nhận hàng"
                                required
                                checked={paymentMethod === 'Thanh toán khi nhận hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Thanh toán khi nhận hàng</span>
                        </label>
                        <label className={styles['thanhtoan-option']}>
                            <input
                                type="radio"
                                name="phuongthuc"
                                value="Chuyển khoản ngân hàng"
                                required
                                checked={paymentMethod === 'Chuyển khoản ngân hàng'}
                                onChange={handlePaymentMethodChange}
                            />
                            <span>Chuyển khoản ngân hàng</span>
                        </label>
                    </div>
                    {paymentMethod === 'Chuyển khoản ngân hàng' && (
                        <div id="qr-pay" className={styles['qr-pay']}>
                            <p className={`flex-center ${styles['qr-pay-text']}`}>Chuyển khoản tại đây</p>
                            <div className={`flex-center`}>
                                <img
                                    className={`flex-center ${styles['img-qr-pay']}`}
                                    src={qrpay}
                                    alt="qr-pay"
                                />
                            </div>
                        </div>
                    )}
                    <button id="dathang" type="submit" style={{ height: 35 }} className={styles['dathang-button']}>
                        <b>Đặt hàng</b>
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Payment;
